import Axios from 'axios';
import React, { useEffect, useState } from 'react'


export default function ChiTietPhim(props) {
    // tạo 1 state chứa thông tin chi tiết phim giá trị ban đầu là object rổng 
    const [chiTietPhim, setChiTietPhim] = useState({});

    //  dùng useEffect để tự động gọi api khi trang chi tiết phim load ra 
    useEffect(async () => {
        //props.match.params.:props này là props của thẻ Route truyền cho component
        //B1: Lấy mã phim từ url 
        const maPhim = props.match.params.maPhim;
        // B2: Dựa vào mã gửi lên api backend lấy dữ liệu phim về và gán vào state ChiTietPhim 
        const result = await Axios({
            url: `http://movie0706.cybersoft.edu.vn/api/QuanLyRap/LayThongTinLichChieuPhim?MaPhim=${maPhim}`,
            method: 'GET'

        });
        console.log('data', result);
        setChiTietPhim(result.data);
        document.title = result.data.tenPhim;

    }, [])

    return (
        <div className="container">
            <div className="row">
                <div className="col-6">
                    <img src={chiTietPhim.hinhAnh} alt={chiTietPhim.hinhAnh} />
                </div>
                <div className="col-6">
                    <table className="table">
                        <tbody>
                            <tr>
                                <td>Tên Phim</td>
                                <td>{chiTietPhim.tenPhim}</td>
                            </tr>
                            <tr>
                                <td>Mô tả</td>
                                <td>{chiTietPhim.moTa}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
            <div className="row mt-5">
                <div className="col-12">
                    <h3>Thông tin lịch chiếu</h3>

                </div>
                <div className="row">
                    <div className="d-flex align-items-start col-4">
                        <div className="nav flex-column nav-pills me-3" id="v-pills-tab" role="tablist" aria-orientation="vertical">
                            <a className="nav-link active" id="v-pills-home-tab" data-bs-toggle="pill" href="#v-pills-home" role="tab" aria-controls="v-pills-home" aria-selected="true">Home</a>
                            <a className="nav-link" id="v-pills-profile-tab" data-bs-toggle="pill" href="#v-pills-profile" role="tab" aria-controls="v-pills-profile" aria-selected="false">Profile</a>
                            <a className="nav-link" id="v-pills-messages-tab" data-bs-toggle="pill" href="#v-pills-messages" role="tab" aria-controls="v-pills-messages" aria-selected="false">Messages</a>
                            <a className="nav-link" id="v-pills-settings-tab" data-bs-toggle="pill" href="#v-pills-settings" role="tab" aria-controls="v-pills-settings" aria-selected="false">Settings</a>
                        </div>
                        <div className="tab-content col-8" id="v-pills-tabContent">
                            <div className="tab-pane fade show active" id="v-pills-home" role="tabpanel" aria-labelledby="v-pills-home-tab">Home</div>
                            <div className="tab-pane fade" id="v-pills-profile" role="tabpanel" aria-labelledby="v-pills-profile-tab">Profile</div>
                            <div className="tab-pane fade" id="v-pills-messages" role="tabpanel" aria-labelledby="v-pills-messages-tab">Messages</div>
                            <div className="tab-pane fade" id="v-pills-settings" role="tabpanel" aria-labelledby="v-pills-settings-tab">Settings</div>
                        </div>
                    </div>

                </div>

            </div>
        </div>
    )
}
